DROP PROCEDURE `SelectServiciosNoFinalizadosIndividuales`; CREATE DEFINER=`root`@`localhost` PROCEDURE `SelectServiciosNoFinalizadosIndividuales`(IN `idTec` INT(10)) NOT DETERMINISTIC READS SQL DATA SQL SECURITY DEFINER SELECT s.idServicios,s.fecha,s.fecha_fin,s.horaInicio,s.horaFin,s.tipoServicio,s.descripcion,s.ubicacion,t.nombre,s.finalizado,s.solucion FROM servicios s INNER JOIN serviciostecnicos st INNER JOIN tecnicos t ON s.idServicios = st.fk_idServicios AND t.idTecnicos = st.fk_idTecnicos WHERE s.finalizado = 0 AND t.idTecnicos = idTec AND s.mostrar = false ORDER BY s.idServicios
